# -*- coding: utf-8 -*-import reclass pnum_elem():    def __init__(self):        self.country = ''        self.number = ''        self.type = ''def url2pnum(url):    return(re.split(u'/', url)[4])def pnum_to_elem(pnum):    elem = pnum_elem()    elem.country = re.match('\w\w', pnum).group()    pnuml = len(pnum)    if re.match(u'\d', pnum[pnuml-1]) == None:        elem.type = pnum[pnuml-1]        end = pnuml - 1    else:        elem.type = pnum[pnuml-2] + pnum[pnuml-1]        end = pnuml - 2    for i in range(2, end):        elem.number += pnum[i]            return(elem)def page_urls_get(fp):    urls = []    body = fp.read()    lines = re.split(u'\n', body)    for line in lines[2:(len(lines) - 1)]:        elements = re.split(u',', line)                if elements[0] == '0':            urls.append(elements[1])        else:            urls.append(elements[len(elements) - 1])    return(urls)def indent(elem, level=0):    i = "\n" + level*"  "    if len(elem):        if not elem.text or not elem.text.strip():            elem.text = i + "  "        if not elem.tail or not elem.tail.strip():            elem.tail = i        for elem in elem:            indent(elem, level+1)        if not elem.tail or not elem.tail.strip():            elem.tail = i    else:        if level and (not elem.tail or not elem.tail.strip()):            elem.tail = i